    function download_versao($url_versao=null) {

      if(empty($url_versao)){
        $validation = new Validation();
        $validation->add(Validation::VALID_ERR_NOT_EXISTS, 'Informe o link de um arquivo válido e tente novamente.');
        throw new BusinessException($validation);
      }

      $file_name = 'bp_versao_tmp.zip';

      if(file_exists(APP_PATH . "/" . $file_name)){
        unlink(APP_PATH . "/" . $file_name);
      }

      set_time_limit(0);
      $fp = fopen (APP_PATH . "/" . $file_name, 'w+');
      $ch = curl_init(str_replace(" ","%20",$url_versao));
      curl_setopt($ch, CURLOPT_FILE, $fp); 
      curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
      Log::escrever(basename(__FILE__), 'Realizando download do arquivo', defined('JOB_PATH'));
      curl_exec($ch); 
      curl_close($ch);
      fclose($fp);

      if(empty(filesize(APP_PATH . "/" . $file_name))){
        unlink(APP_PATH . "/" . $file_name);
      }

      return file_exists(APP_PATH . "/" . $file_name);
    }

    function criar_backup_versao() {

      $file_name = 'bkp_versao_bp.zip';

      $rootPath = realpath(APP_PATH);

      $zip = new ZipArchive();
      $zip->open(APP_PATH . '/' . $file_name, ZipArchive::CREATE | ZipArchive::OVERWRITE);

      $files = new RecursiveIteratorIterator(
        new RecursiveDirectoryIterator($rootPath),
        RecursiveIteratorIterator::LEAVES_ONLY
      );

      foreach ($files as $name => $file)
      {
        if (!$file->isDir())
        {
          $filePath = $file->getRealPath();
          $relativePath = substr($filePath, strlen($rootPath) + 1);
          
          if(substr($relativePath, -7) !== "tmp.zip"){
            $zip->addFile($filePath, $relativePath);
          }
        }
      }

      $zip->close();

      return file_exists(APP_PATH . "/" . $file_name);
    }

    function descompactar_versao() {
      $file_name = 'bp_versao_tmp.zip';
      
      if(file_exists(APP_PATH . "/" . $file_name)){
        $zip = new ZipArchive;
        if ($zip->open(APP_PATH . "/" . $file_name) === TRUE) {
          Log::escrever(basename(__FILE__), 'Descompactando arquivo da versão', defined('JOB_PATH'));
          $zip->extractTo(APP_PATH);
          $zip->close();
          return true;
        }
      }
      
      return false;
    }

    function restaurar_backup_versao() {
      $file_name = 'bkp_versao_bp.zip';
      $file_name_tmp = 'bkp_versao_bp_tmp.zip';
      if(file_exists(APP_PATH . "/" . $file_name)){
        if(rename(APP_PATH . "/" . $file_name, APP_PATH . "/" . $file_name_tmp)){
          $this->criar_backup_versao();

          $zip = new ZipArchive;
          if ($zip->open(APP_PATH . "/" . $file_name_tmp) === TRUE) {
            Log::escrever(basename(__FILE__), 'Descompactando arquivo de backup', defined('JOB_PATH'));
            $zip->extractTo(APP_PATH);
            $zip->close();
            unlink(APP_PATH . "/" . $file_name_tmp);
            return true;
          }
        }
      }
            
      return false;
    }

