  function dbu_exec($dbversao_index) {
      $updSQL = $this->get_instrucoes();

      $sql = "";

      if (isset($updSQL[$dbversao_index])) {
        try {
              if (gettype($updSQL[$dbversao_index]) == "string") {
                $sql = $updSQL[$dbversao_index];
                $query = DB::exec($sql);
              }
              else {
                $updSQL[$dbversao_index]();
              }

              // insert atualizacao
              $this->salvar($dbversao_index);

              self::salvar_historico_versao_corrente_bp();

              // se for o último índice do dbu, verifico se possui tema e aplico
              if (array_keys($updSQL)[count($updSQL)-1] == $dbversao_index) {
                  $this->ajuste_tema();
              }

              return array('executado' => $dbversao_index);
          }
          catch (Exception $e) {
            
              if (get_class($e) == "BusinessException") {
                $e->validation->add(Validation::VALID_ERR_NOT_EXISTS, $sql);
                throw $e;
              }
              $validation = new Validation();
              $validation->add(Validation::VALID_ERR_NOT_EXISTS, $e->getMessage());
              $validation->add(Validation::VALID_ERR_NOT_EXISTS, $sql);
              throw new BusinessException($validation);
          }
      }
      else {
          $validation = new Validation();
          $validation->add(Validation::VALID_ERR_NOT_EXISTS, 'Índice do DBU #' . $dbversao_index . ' inválido.');
          throw new BusinessException($validation);
      }
  }

  function dbu_exec_recurso($dbrecurso_index) {
      $updSQL = Atualizacao_Recursos_DAO::get_recursos();

      if (!isset($updSQL[$dbrecurso_index])) {
          $validation = new Validation();
          $validation->add(Validation::VALID_ERR_NOT_EXISTS, 'Índice do Recurso ' . $dbrecurso_index . ' inválido');
          throw new BusinessException($validation);
      }
      else {
          $sql = '';
          try {
              $sql = $updSQL[$dbrecurso_index];
              $query = DB::exec($sql);

              self::salvar_historico_versao_corrente_bp();

              // se for o último índice do dbu, verifico se possui tema e aplico
              if (array_keys($updSQL)[count($updSQL)-1] == $dbrecurso_index) {
                  $this->ajuste_tema();
              }

              return array('executado' => $dbrecurso_index);
          }
          catch (Exception $e) {
              $validation = new Validation();
              $validation->add(Validation::VALID_ERR_NOT_EXISTS, $e->getMessage());
              $validation->add(Validation::VALID_ERR_NOT_EXISTS, $sql);
              throw new BusinessException($validation);
          }
      }
  }

  function dbu_reindexar($tabela) {

      $sql_reindex = '';
      try {
          $db_name = DB_NAME;
          $sql_reindex = "ALTER TABLE `{$db_name}`.`{$tabela}` ENGINE=InnoDB;";
          DB::exec($sql_reindex);

          return array('executado' => $tabela);
      }
      catch (Exception $e) {
          $validation = new Validation();
          $validation->add(Validation::VALID_ERR, $e->getMessage());
          $validation->add(Validation::VALID_ERR, $sql_reindex);
          throw new BusinessException($validation);
      }

  }

