  function dbu_cli($command_line_interface) {

      Log::escrever(basename(__FILE__), "Next BP DBU " . VERSION . " " . str_replace(['http://', 'https://'], ['',''], APP_URL), $command_line_interface);

      Log::escrever(basename(__FILE__), 'Atualização da estrutura do banco de dados iniciada', $command_line_interface);
      Log::escrever(basename(__FILE__), 'Verificando atualizações pendentes da estrutura da base de dados ', $command_line_interface);

      // instancio a atualizacao
      $instrucoes = $this->encontrar_atualizacoes_pendentes();
      $qtd_instrucoes = sizeof($this->dbu_pendentes());
      
      //Log::escrever(basename(__FILE__), "DB Recursos.......... #".($recursos-1), $command_line_interface);

      if ($qtd_instrucoes > 0) {
          Log::escrever(basename(__FILE__), "Atualizando estrutura do banco de dados", $command_line_interface);
          try {
            foreach ($instrucoes as $i => $instrucao_hash) {
                Log::escrever(basename(__FILE__), "Executando instrucao #".$instrucao_hash, $command_line_interface);
                $this->dbu_exec($instrucao_hash);
            }
          }
          catch (BusinessException $e) {
              http_response_code(500);
              Log::escrever(basename(__FILE__), json_encode($e, JSON_PRETTY_PRINT), true, Log::CLI_DANGER);
              exit;
          }

          Log::escrever(basename(__FILE__), 'Atualizacao de estrutura do banco de dados concluida', $command_line_interface);
      }
      else {
          Log::escrever(basename(__FILE__), "A estrutura da base de dados ja se encontra atualizada", $command_line_interface);
      }

      try {
          Log::escrever(basename(__FILE__), 'Atualizacao de procedimentos e funcoes do banco de dados iniciada', $command_line_interface);
          $recursos = Atualizacao_Recursos_DAO::get_recursos();
          foreach ($recursos as $instrucao_hash => $instrucao) {
              Log::escrever(basename(__FILE__), "Executando recurso #".$instrucao_hash, $command_line_interface);
              $this->dbu_exec_recurso($instrucao_hash);
          }
          Log::escrever(basename(__FILE__), 'Atualizacao de procedimentos e funcoes do banco de dados concluida', $command_line_interface);
      }
      catch (BusinessException $e) {
          http_response_code(500);
          Log::escrever(basename(__FILE__), json_encode($e, JSON_PRETTY_PRINT), true, Log::CLI_DANGER);
          exit;
      }

      Log::escrever(basename(__FILE__), 'Atualizacao de estrutura procedimentos e funcoes do banco de dados concluida', true);
      print PHP_EOL;

  }

  function salvar($hash) {
      // INSERT dbversao anterior
      $sql = "INSERT INTO atualizacao (hash, versao, execucao) VALUES (:hash, :versao, :execucao)";
      $params[':hash'] = $hash;
      $params[':versao'] = VERSION;
      $params[':execucao'] = Util::DateNow();
      DB::exec($sql, $params);
  }

  static function salvar_historico_versao_corrente_bp() {
      // se essa execução do PHP já executou essa função, então não precisa executa-la novamente
      if (self::$ja_salvou_historico_versao_corrente_bp) return false;

      // verifica a última versão do BP
      $sql = "SELECT versao FROM atualizacao_historico ORDER BY id DESC LIMIT 1";
      $query = DB::query($sql);

      // se não tiver histórico
      if (!DB::has_rows($query)
          // ou for diferente da última versão
          || $query[0]['versao'] != VERSION) {
          // insere a última versão
          $sql = "INSERT INTO atualizacao_historico (versao, execucao) VALUES (:versao, :execucao)";
          $params[':versao'] = VERSION;
          $params[':execucao'] = Util::DateNow();
          DB::exec($sql, $params);
      }

      self::$ja_salvou_historico_versao_corrente_bp = true;
  }

