  function prepara_tabela_atualizacoes() {
      if (!DB::existe_tabela("atualizacao")) {
          Log::escrever(basename(__FILE__), 'Criando tabela para controle de atualizacoes ', defined('JOB_PATH'));
        
          $sql = "CREATE TABLE `atualizacao` (
                    `hash` varchar(255) NOT NULL,
                    `versao` varchar(50) NULL,
                    `execucao` DATETIME NULL,
                    PRIMARY KEY (`hash`)
                  ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci ";
          DB::exec($sql);
      }

      if (!DB::existe_tabela("atualizacao_historico")) {
        Log::escrever(basename(__FILE__), 'Criando tabela para controle de versoes do BP ', defined('JOB_PATH'));
      
        $sql = "CREATE TABLE `atualizacao_historico` (
                  `id` int(11) NOT NULL AUTO_INCREMENT,
                  `versao` varchar(50) NULL,
                  `execucao` DATETIME NULL,
                  PRIMARY KEY (`id`)
                ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci ";
        DB::exec($sql);
    }

      if (DB::existe_tabela("parametro")) {
          $parametro_dao = new Parametro_DAO;
          $dbversao = $parametro_dao->get('dbversao', -1);

          if ((int)$dbversao > 0) {
              Log::escrever(basename(__FILE__), 'Carga inicial no controle de atualizacoes ', defined('JOB_PATH'));
              DB::begin_transaction();
              for ($hash = 0; $hash <= $dbversao; $hash++) {
                  // INSERT dbversao anterior
                  $sql = "INSERT INTO atualizacao (hash) VALUES (:hash)";
                  $params[':hash'] = $hash;
                  try {
                      DB::exec($sql, $params);
                  }
                  catch(Exception $e) { }
              }
              DB::commit();

              $parametro_dao = new Parametro_DAO();
              $parametro_dao->set('dbversao', 'DESCONTINUADO - utilizar a tabela "atualizacao"');
          }
      }
  }

  function encontrar_atualizacoes_pendentes() {
      $this->prepara_tabela_atualizacoes();  
      $sql = "SELECT hash FROM atualizacao";
      $query = DB::query($sql);
      $hashs_executadas = [];
      $pendentes = [];
      foreach ($query as $query_index => $row) {
          $hashs_executadas[] = $row['hash'];
      }
      
      $instrucoes = $this->get_instrucoes();

      foreach ($instrucoes as $instrucao_hash => $instrucao) {
          if (in_array($instrucao_hash, $hashs_executadas)) {
            continue;
          }
          $pendentes[] = $instrucao_hash;
      }

      return $pendentes;
  }

  private static function phpinfo() {
    ob_start();
    phpinfo();
    $phpinfo = ob_get_contents();
    ob_get_clean();
    return $phpinfo;
  }

  private static function mysql_version() {
      return DB::query(" SELECT VERSION() AS mysql_version; ")[0]['mysql_version'];
  }

  private static function innodb_status_html() {
      $sql = 'SHOW ENGINE INNODB STATUS';
      try {
          $lista = DB::query($sql);
      }
      catch (BusinessException $e) {
          return nl2br($sql . PHP_EOL . $e->validation->toString());
      }

      $html = '';
      foreach ($lista as $item) {
          $item_html = nl2br($item['Status']);
          $destacar = 'MySQL thread id';
          $item_html = str_replace($destacar, sprintf('<mark>%s</mark>', $destacar), $item_html);
          $html .= $item_html;
      }
      return $html;
  }

  function informacoes_servidor() {
