$updSQL[360] = '
CREATE PROCEDURE `sp_sequencia_solicitacao_processo`()
BEGIN
    DECLARE done INT DEFAULT FALSE;
  DECLARE var_contador INT DEFAULT 0;
    DECLARE var_solicitacao_id INT DEFAULT 0;
    DECLARE var_processo_id INT DEFAULT 0;
    DECLARE var_old_processo INT DEFAULT 0;

  DECLARE cursor_solicitacoes CURSOR FOR
    (
    SELECT
      solicitacao.id AS solicitacao_id,
      versao_processo.processo_id
    FROM
      solicitacao
    INNER JOIN versao_processo ON versao_processo.id = solicitacao.versao_processo_id
    ORDER BY
      processo_id, solicitacao.id
  );

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

  OPEN cursor_solicitacoes;
  read_loop: LOOP

    FETCH cursor_solicitacoes INTO var_solicitacao_id, var_processo_id;

    IF ((var_old_processo > 0 AND var_processo_id <> var_old_processo) OR (done)) THEN

      UPDATE
        processo
      SET
        sequencia_solicitacoes = var_contador
      WHERE
        id = var_old_processo;

      SET var_contador = 0;

    END IF;

    SET var_contador = var_contador + 1;

    IF done THEN
      LEAVE read_loop;
    END IF;

    UPDATE
      solicitacao
    SET
      numero = var_contador
    WHERE
      id = var_solicitacao_id;

        SET var_old_processo = var_processo_id;

    END LOOP read_loop;

  CLOSE cursor_solicitacoes;

END
