$updSQL[875] = "DROP FUNCTION IF EXISTS fn_converter_horas_para_segundos";

$updSQL[876] = "

  CREATE FUNCTION fn_converter_segundos_para_horas(Segundos INT) RETURNS VARCHAR(12)
      DETERMINISTIC
  BEGIN

      DECLARE iHoras INT;
      DECLARE iMinutos INT;
      DECLARE iSegundos INT;

      DECLARE cHoras VARCHAR(6);
      DECLARE cMinutos VARCHAR(2);
      DECLARE cSegundos VARCHAR(2);

      SET iHoras = TRUNCATE(Segundos / 3600, 0);
      SET iMinutos = TRUNCATE(Segundos / 60, 0) - TRUNCATE(Segundos / 3600, 0) * 60;
      SET iSegundos = Segundos - (TRUNCATE(Segundos / 60, 0) * 60);

      SET cHoras = CAST(ABS(iHoras) AS CHAR(5));
      SET cMinutos = CAST(ABS(iMinutos) AS CHAR(2));
      SET cSegundos = CAST(ABS(iSegundos) AS CHAR(2));

      IF LENGTH(cHoras) < 2 THEN
          SET cHoras = CONCAT(REPEAT('0', 2 - LENGTH(cHoras)), RTrim(cHoras));
      END IF;

      IF (iHoras < 0) THEN
      RETURN CONCAT('-', cHoras, ':', CONCAT(REPEAT('0', 2 - LENGTH(cMinutos)), RTrim(cMinutos)), ':', CONCAT(REPEAT('0', 2 - LENGTH(cSegundos)), RTRIM(cSegundos)));
      END IF;

      RETURN CONCAT(cHoras, ':', CONCAT(REPEAT('0', 2 - LENGTH(cMinutos)), RTrim(cMinutos)), ':', CONCAT(REPEAT('0', 2 - LENGTH(cSegundos)), RTRIM(cSegundos)));

  END

";

$updSQL[877] = "

  CREATE FUNCTION fn_converter_horas_para_segundos(Horas VARCHAR(12)) RETURNS INT
      DETERMINISTIC
  BEGIN

      DECLARE iHoras INT;
      DECLARE iMinutos INT;
      DECLARE iSegundos INT;
      DECLARE iRetorno INT;

      DECLARE cHoras VARCHAR(6);
      DECLARE cMinutos VARCHAR(2);
      DECLARE cMinutosSegundos VARCHAR(6);
      DECLARE cSegundos VARCHAR(2);

      IF Horas = '' OR Horas = '0' THEN
          SET iRetorno = 0;
      ELSE

          SET cHoras = Substring(Horas, 1, TRUNCATE(INSTR(Horas, ':'), 0) - 1);
          SET cMinutosSegundos = Substring(Horas, TRUNCATE(INSTR(Horas, ':'), 0) + 1, LENGTH(Horas) - TRUNCATE(INSTR(':', Horas), 0));
          SET cMinutos = Substring(cMinutosSegundos, 1,  2);

          IF (TRUNCATE(INSTR(cMinutosSegundos, ':'), 0) <> 0) THEN
              SET cSegundos = Substring(cMinutosSegundos, TRUNCATE(INSTR(cMinutosSegundos, ':'), 0) + 1, LENGTH(cMinutosSegundos) - TRUNCATE(INSTR(cMinutosSegundos, ':'), 0));
          ELSE
              SET cSegundos = '0';
          END IF;

          SET iHoras = TRUNCATE(cHoras, 0);
          SET iMinutos = TRUNCATE(cMinutos, 0);
          SET iSegundos = TRUNCATE(cSegundos, 0);

          SET iRetorno = (iHoras * 3600) + (iMinutos * 60) + iSegundos;

      END IF;

      RETURN iRetorno;

  END

";

