$updSQL[902] = "

CREATE PROCEDURE sp_recalcular_apontamentos()
BEGIN

  --
    -- @author Diego Henrique Turco
    --

  DECLARE done INT DEFAULT FALSE;
    DECLARE var_apontamento_projeto_atividade_id INT;
    DECLARE var_horas_totais TIME;
    DECLARE var_projeto_atividade_id INT;
    DECLARE var_usuario_id INT;

    --
    -- CURSOR APONTAMENTO_PROJETO_ATIVIDADE.
    -- CALCULANDO AS HORAS TOTAIS DO APONTAMENTO ATIVIDADE DE ACORDO COM A CONFIGURAÇÃO DE TRASLADO LOCALIZADA NO PROJETO.
    --
    DECLARE cursor_apontamento_atividade CURSOR FOR
  SELECT
    apontamento_projeto_atividade.id,
        apontamento_projeto.usuario_id,
        apontamento_projeto_atividade.projeto_atividade_id,
    CASE projeto.considerar_traslado
      WHEN 'S' THEN fn_converter_segundos_para_horas(fn_converter_horas_para_segundos(COALESCE(apontamento_projeto_atividade.horas, 0)) + (TRUNCATE(TRUNCATE(fn_converter_horas_para_segundos(COALESCE(apontamento_projeto.horas_traslado, 0)), 0) / (SELECT COUNT(*) FROM apontamento_projeto_atividade sub WHERE sub.apontamento_projeto_id = apontamento_projeto.id), 0)))
      ELSE apontamento_projeto_atividade.horas
    END AS apontamento_projeto_atividade_horas_totais
  FROM
    apontamento_projeto_atividade
  LEFT JOIN apontamento_projeto ON apontamento_projeto.id = apontamento_projeto_id AND apontamento_projeto.excluido = 'N'
  LEFT JOIN projeto ON projeto.id = apontamento_projeto.projeto_id AND projeto.excluido = 'N'
  WHERE
    apontamento_projeto_atividade.excluido = 'N';

  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    --
    -- ZERANDO O VALOR TRABALHADO PARA RECALCULAR
    --
  UPDATE
    projeto_atividade_usuario
  SET
    valor_trabalhado = 0.00
  WHERE
    projeto_atividade_id > 0 AND
    usuario_id > 0;

  --
  -- ABRINDO O CURSOR.
    --
  OPEN cursor_apontamento_atividade;

    --
    -- PERCORRENDO OS APONTAMENTOS DE ATIVIDADES DE PROJETO.
    --
  read_loop: LOOP

    FETCH cursor_apontamento_atividade INTO var_apontamento_projeto_atividade_id, var_usuario_id, var_projeto_atividade_id, var_horas_totais;

    IF done THEN
      LEAVE read_loop;
    END IF;

        --
        -- CONSULTANDO O VALOR PREVISTO E VALOR HORA DO EXECUTOR.
        --
        SET @valor_hora =
      (SELECT
        CAST(projeto_atividade_usuario.valor_hora AS DECIMAL(10, 2)) AS valor_hora
      FROM
        projeto_atividade_usuario
      WHERE
        projeto_atividade_usuario.projeto_atividade_id = var_projeto_atividade_id AND
        projeto_atividade_usuario.usuario_id = var_usuario_id LIMIT 1);

        SET @valor_previsto =
      (SELECT
        CAST((fn_converter_horas_para_segundos(horas_previstas) / 3600) * CAST(projeto_atividade_usuario.valor_hora AS DECIMAL(10, 2)) AS DECIMAL(10, 2)) AS valor_previsto
      FROM
        projeto_atividade_usuario
      WHERE
        projeto_atividade_usuario.projeto_atividade_id = var_projeto_atividade_id AND
        projeto_atividade_usuario.usuario_id = var_usuario_id LIMIT 1);

    --
        -- CALCULANDO O VALOR TRABALHADO DO EXECUTOR.
        --
        SET @valor_trabalhado = (fn_converter_horas_para_segundos(var_horas_totais) / 3600) * @valor_hora;

    UPDATE
      projeto_atividade_usuario
    SET
      valor_trabalhado = COALESCE(valor_trabalhado, 0) + COALESCE(@valor_trabalhado, 0),
            valor_previsto = @valor_previsto
    WHERE
      projeto_atividade_id = var_projeto_atividade_id AND
      usuario_id = var_usuario_id;

        --
        -- ATUALIZANDO AS HORAS_TOTAIS DA TABELA APONTAMENTO_PROJETO_ATIVIDADE.
        --
        UPDATE
      apontamento_projeto_atividade
    SET
      horas_totais = var_horas_totais,
            valor_trabalhado = @valor_trabalhado
    WHERE
      id = var_apontamento_projeto_atividade_id;

  END LOOP read_loop;

  CLOSE cursor_apontamento_atividade;

END

";

