      DECLARE _sintetica_id INT;
        DECLARE _pai_id INT;
      DECLARE DONE INT DEFAULT FALSE;

      DECLARE status_sinteticas_inicial CURSOR FOR

        SELECT id, projeto_atividade_sintetica_pai_id
            FROM projeto_atividade
            WHERE projeto_id = PARAM_PROJETO_ID
            AND tipo_atividade = 'S'
            AND excluido = 'N'
            AND (projeto_atividade_sintetica_pai_id = PARAM_ID
            OR id = PARAM_ID);

      DECLARE CONTINUE HANDLER FOR NOT FOUND SET DONE = TRUE;

      OPEN status_sinteticas_inicial;
        SET NIVEL_ATUAL = NIVEL_ATUAL +1;
        -- REALIZO O LAÇO, NECESSARIO PARA CALCULAR STATUS INICIAL;
        PERCORRE_SINTETICAS_PAI:LOOP

          FETCH status_sinteticas_inicial
                INTO _sintetica_id, _pai_id;

                IF(DONE = TRUE) THEN
            LEAVE PERCORRE_SINTETICAS_PAI;
