$updSQL[962] = "DROP FUNCTION IF EXISTS fn_sintetica_status_final;";
$updSQL[963] = "
    CREATE FUNCTION fn_sintetica_status_final(status_original INT, novo_status INT) RETURNS int(11)
    DETERMINISTIC
    PROCESSO_IDENTIFICACAO_STATUS:BEGIN

        -- FLAG RESPONSÁVEL POR CONTROLAR LOOP/CURSOR
        DECLARE done INT DEFAULT FALSE;
        DECLARE status_atual INT ;
        DECLARE proximo_status INT;
        DECLARE atividades_analiticas_calculo CURSOR FOR

        SELECT status FROM (SELECT status_original  as status
        UNION
        SELECT novo_status as status) AS teste
        GROUP BY status ORDER BY (CASE WHEN status = 5 THEN 0 WHEN status = 2 THEN 1 WHEN status = 4 THEN 2 WHEN status = 1 THEN 3 ELSE 4 END) ASC;

        -- DECLARO O HANDLER QUE IRÁ MANIPULAR O CURSOR;
        DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
            OPEN atividades_analiticas_calculo;
                SET @status_atual = 0;
                 -- REALIZO A LEITURA DOS ITENS DO LOOP
                processa_looping:LOOP
                    FETCH atividades_analiticas_calculo
                    INTO proximo_status;

                    -- SE NENHUMA LINHA FOR ENCONTRADA CORTE O FLUXO;
                    IF(done = TRUE) THEN
                        IF(@status_atual = 0) THEN
                            SET @status_atual = 4;
                        END IF;
                        LEAVE processa_looping;
                    END IF;

                    /*
                    1) atraso        0 --- sai DO LOOPING
                    2) andamento     1 -- PREDOMINA
                    3) não iniciado  2 -- SE NÃO TEM NENHUMA EM ANDAMENTO PREDOMINA
                    4) programado    3 -- SE NÃO TEM NENHUM INICIADO PREDOMINA
                    5) concluído     4 -- SE NÃO TEM NENHUMA PROGRAMADA NEM NADA PREDOMINA
                    */
                    -- Prioriza o Atrasado
                    IF(proximo_status = 5) THEN
                        SET @status_atual =5;
                        LEAVE processa_looping;
                    -- SE ESTÁ EM ANDAMENTO
                    ELSEIF(proximo_status = 2) THEN
                        SET @status_atual = 2;
                        LEAVE processa_looping;
                    ELSEIF(proximo_status = 4 ) THEN
                        SET @status_atual = 4;
                    ELSEIF(proximo_status = 1 && @status_atual != 3 ) THEN
                        SET @status_atual = 1;
                    ELSEIF(proximo_status = 1 && @status_atual = 3 || proximo_status = 3 && @status_atual = 1) THEN
                        SET @status_atual = 2;
                    ELSEIF(proximo_status = 1 && @status_atual = 4) THEN
                        SET @status_atual = 2;
                    ELSEIF(proximo_status = 3 && @status_atual = 4) THEN
                        SET @status_atual = 2;
                    ELSEIF(proximo_status = 3 && @status_atual = 0) THEN
                        SET @status_atual = 3;

                    END IF;
                END LOOP processa_looping;

            CLOSE atividades_analiticas_calculo;

            RETURN @status_atual;

        END PROCESSO_IDENTIFICACAO_STATUS;
  ";
