$updSQL[993] = "

CREATE PROCEDURE sp_calcular_dados_atividade_projeto(param_projeto_id INT, param_projeto_atividade_id INT)
BEGIN

  --
    -- @author Diego Henrique Turco
    --

  DECLARE done INT DEFAULT FALSE;
    DECLARE var_projeto_atividade_id INT;
    DECLARE var_data_conclusao DATE;
    DECLARE var_porcentagem_conclusao INT;

    --
    -- HORAS
    --
    DECLARE var_horas_previstas VARCHAR(20);
    DECLARE var_horas_trabalhadas VARCHAR(20);
    DECLARE var_horas_traslado VARCHAR(20);
    DECLARE var_saldo_horas VARCHAR(20);

    --
    -- VALORES
    --
    DECLARE var_valor_previsto DECIMAL(10, 2);
    DECLARE var_valor_trabalhado DECIMAL(10, 2);
    DECLARE var_saldo_valor DECIMAL(10, 2);
    DECLARE var_saldo_horas_percentual DECIMAL(10, 2);

    --
    -- CURSOR
    --
  DECLARE cursor_projeto_atividade CURSOR FOR
  SELECT
    TMP2.projeto_atividade_id,
    TMP2.horas_previstas,
        TMP2.valor_previsto,
    TMP2.horas_trabalhadas,
    TMP2.valor_trabalhado,
    TMP2.horas_traslado,
    TMP2.saldo_horas,
    COALESCE(TMP2.valor_trabalhado, 0) - COALESCE(TMP2.valor_previsto, 0) AS saldo_valor,
    TMP2.saldo_horas_percentual,
        TMP2.data_conclusao,
        TMP2.porcentagem_conclusao
  FROM
    (

      SELECT
        TMP.projeto_atividade_id,
        TMP.horas_previstas,
        TMP.valor_previsto,
        TMP.horas_trabalhadas,
        TMP.valor_trabalhado,
        TMP.horas_traslado,
        fn_converter_segundos_para_horas(fn_converter_horas_para_segundos(COALESCE(TMP.horas_previstas, 0)) - fn_converter_horas_para_segundos(COALESCE(TMP.horas_trabalhadas, 0))) AS saldo_horas,
        CAST(fn_converter_segundos_para_horas(((fn_converter_horas_para_segundos(COALESCE(TMP.horas_previstas, 0)) - fn_converter_horas_para_segundos(COALESCE(TMP.horas_trabalhadas, 0))) * 100) / fn_converter_horas_para_segundos(COALESCE(TMP.horas_previstas, 0))) AS DECIMAL(10, 2)) AS saldo_horas_percentual,
                TMP.data_conclusao,
                TMP.porcentagem_conclusao
      FROM
        (

          SELECT
            projeto_atividade.id AS projeto_atividade_id,

            --
            -- HORAS PREVISTAS DA ATIVIDADE
            --
            (SELECT
              fn_converter_segundos_para_horas(SUM(fn_converter_horas_para_segundos(projeto_atividade_usuario.horas_previstas)))
            FROM
              projeto_atividade_usuario
            WHERE
              projeto_atividade_usuario.projeto_atividade_id = projeto_atividade.id) AS horas_previstas,

            --
            -- VALOR PREVISTO DA ATIVIDADE
            --
            (SELECT
              SUM(projeto_atividade_usuario.valor_previsto)
            FROM
              projeto_atividade_usuario
            WHERE
              projeto_atividade_usuario.projeto_atividade_id = projeto_atividade.id) AS valor_previsto,

            --
            -- HORAS TRABALHADAS, VALOR TRABALHADO, HORAS TRASLADO REFERENTE AOS APONTAMENTOS DE ATIVIDADES
            --
            fn_converter_segundos_para_horas(SUM(fn_converter_horas_para_segundos(apontamento_projeto_atividade.horas_totais))) AS horas_trabalhadas,
            SUM(apontamento_projeto_atividade.valor_trabalhado) AS valor_trabalhado,
            fn_converter_segundos_para_horas(SUM(fn_converter_horas_para_segundos(COALESCE(apontamento_projeto_atividade.horas_totais, 0))) - SUM(fn_converter_horas_para_segundos(COALESCE(apontamento_projeto_atividade.horas, 0)))) horas_traslado,
            projeto_atividade.conclusao AS data_conclusao,
            projeto_atividade.porcentagem AS porcentagem_conclusao
          FROM
            projeto_atividade
          LEFT JOIN apontamento_projeto_atividade ON (projeto_atividade.id = apontamento_projeto_atividade.projeto_atividade_id) AND (apontamento_projeto_atividade.excluido = 'N')
          WHERE
            projeto_atividade.excluido = 'N' AND
            ((param_projeto_id IS NULL AND param_projeto_atividade_id IS NULL) OR
            (projeto_atividade.projeto_id = param_projeto_id OR projeto_atividade.id = param_projeto_atividade_id))
          GROUP BY
            projeto_atividade.id

        )
      AS TMP

    ) AS TMP2;


    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

  OPEN cursor_projeto_atividade;

    --
    -- RECALCULANDO OS VALORES DA ATIVIDADE DE PROJETO
    --
  read_loop: LOOP

    FETCH cursor_projeto_atividade INTO var_projeto_atividade_id, var_horas_previstas, var_valor_previsto, var_horas_trabalhadas, var_valor_trabalhado, var_horas_traslado, var_saldo_horas, var_saldo_valor, var_saldo_horas_percentual, var_data_conclusao, var_porcentagem_conclusao;

    IF done THEN
      LEAVE read_loop;
    END IF;

        IF (var_data_conclusao IS NOT NULL) THEN
      SET var_porcentagem_conclusao = 100;
        END IF;

    UPDATE
      projeto_atividade
    SET
            horas_previstas   = var_horas_previstas,
            valor_hora        = var_valor_previsto,
            horas_trabalhadas = var_horas_trabalhadas,
      valor_trabalhado  = var_valor_trabalhado,
            horas_traslado    = var_horas_traslado,
            saldo_horas       = var_saldo_horas,
            saldo_valor       = var_saldo_valor,
            saldo_percentual  = var_saldo_horas_percentual,
            porcentagem       = var_porcentagem_conclusao
    WHERE
      id = var_projeto_atividade_id;

  END LOOP read_loop;

  CLOSE cursor_projeto_atividade;

END

";

