  CREATE FUNCTION fn_calcular_status_temporario(PARAM_PAI_ID INT, PARAM_AUX INT ) RETURNS int(11)
    DETERMINISTIC
    ESCOPO:BEGIN
                INICIAL:BEGIN
                    IF(PARAM_AUX IS NULL) THEN
                        SET PARAM_AUX = PARAM_PAI_ID;
                    END IF;
                END;
                PRINCIPAL:BEGIN
                    DECLARE status_atual INT DEFAULT 0;
                    DECLARE proximo_status INT;
                    DECLARE alterado INT DEFAULT FALSE;

                    -- FLAG RESPONSÁVEL POR CONTROLAR LOOP/CURSOR
                    DECLARE done INT DEFAULT FALSE;
                    DECLARE atividades_sinteticas CURSOR FOR

                        SELECT
                            status_id
                        FROM tmp_sintetica_status
                        WHERE (pai_id = PARAM_PAI_ID OR sintetica_id = PARAM_AUX)
                        OR sintetica_id = PARAM_PAI_ID

                        GROUP BY status_id
                        ORDER BY (CASE WHEN status_id = 5 THEN 0 WHEN status_id = 2 THEN 1 WHEN status_id = 4 THEN 2 WHEN status_id = 1 THEN 3  WHEN status_id = 3 THEN 4 ELSE 5 END) ASC;

                    -- DECLARO O HANDLER QUE IRÁ MANIPULAR O CURSOR;
                    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
                    OPEN atividades_sinteticas;
                        SET @status_atual = 0;

                    -- REALIZO A LEITURA DOS ITENS DO LOOP
                        processa_looping:LOOP
                            -- SE NENHUMA LINHA FOR ENCONTRADA CORTE O FLUXO;
                            IF(done = TRUE) THEN
                                IF(@status_atual = 0) THEN
                                    SET @status_atual = 4;

                                END IF;
                                LEAVE processa_looping;
                            END IF;

                            FETCH atividades_sinteticas
                            INTO proximo_status;

                            /*
                            1) atraso        0 --- sai DO LOOPING
                            2) andamento     1 -- PREDOMINA
                            3) não iniciado  2 -- SE NÃO TEM NENHUMA EM ANDAMENTO PREDOMINA
                            4) programado    3 -- SE NÃO TEM NENHUM INICIADO PREDOMINA
                            5) concluído     4 -- SE NÃO TEM NENHUMA PROGRAMADA NEM NADA PREDOMINA
                            */
                            -- Prioriza o Atrasado
                            IF(proximo_status = 5) THEN
                                SET @status_atual =5;
                                LEAVE processa_looping;
                            -- SE ESTÁ EM ANDAMENTO
                            ELSEIF(proximo_status = 2) THEN
                                SET @status_atual = 2;
                                LEAVE processa_looping;
                            ELSEIF(proximo_status = 4) THEN
                                SET @status_atual = 4;
                            ELSEIF(proximo_status = 1 && @status_atual != 3 && @status_atual != 4) THEN
                                SET @status_atual = 1;
                            ELSEIF(proximo_status = 1 && @status_atual = 3 || proximo_status = 3 && @status_atual = 1) THEN
                                SET @status_atual = 2;
                            ELSEIF(proximo_status = 1 && @status_atual = 4) THEN
                                SET @status_atual = 2;
                            ELSEIF(proximo_status = 3 && @status_atual = 4) THEN
                                SET @status_atual = 2;
                            ELSEIF(proximo_status = 3 && @status_atual = 0) THEN
                                SET @status_atual = 3;
                            ELSEIF(proximo_status = 0 && @status_atual = 0) THEN
                                SET @status_atual = 4;

                            END IF;
                        END LOOP processa_looping;
                    CLOSE atividades_sinteticas;
                END PRINCIPAL;
                        RETURN @status_atual;
            END ESCOPO;
";
