    DECLARE ANDAMENTO INT DEFAULT 2;
    DECLARE CONCLUIDO INT DEFAULT 3;
    DECLARE NAO_INICIADO INT DEFAULT 4;
    DECLARE ATRASADO INT DEFAULT 5;
