    --                                       ---
        SELECT
            oportunidade.situacao,
            funil_vendas_etapa.estagnado,
            funil_vendas_etapa.dias_estagnado
        INTO
            SITUACAO_OPORTUNIDADE,
            CONSIDERA_ESTAGNADO,
            QTD_DIAS_PARA_ESTAGNADO
        FROM
            oportunidade
        INNER JOIN funil_vendas_etapa ON funil_vendas_etapa.id = oportunidade.funil_vendas_etapa_id AND funil_vendas_etapa.excluido = 'N'
        WHERE
            oportunidade.excluido = 'N' AND
            oportunidade.id =  param_oportunidade_id;
        
        SET SITUACAO_ATUAL = SITUACAO_OPORTUNIDADE;
        
        -- VERIFICO O STATUS
        IF (SITUACAO_OPORTUNIDADE = GANHA OR SITUACAO_OPORTUNIDADE = PERDIDA) THEN
            SET CALCULAR_ESTAGNADO = FALSE;
            SET SITUACAO_ATUAL = SITUACAO_OPORTUNIDADE;

        ELSEIF(SITUACAO_OPORTUNIDADE <> GANHA AND SITUACAO_OPORTUNIDADE <> PERDIDA AND CONSIDERA_ESTAGNADO = 'S') THEN
            SET CALCULAR_ESTAGNADO = TRUE;
            SET SITUACAO_ATUAL = SITUACAO_OPORTUNIDADE;
        END IF;

        --
        -- VERIFICO SE O CALCULO DE ESTAGNADO DEVE SER FEITO.
        --
        
        IF(CALCULAR_ESTAGNADO = TRUE) THEN
         /**
         * IDENTIFICO A ULTIMA INTERAÇÃO
         * CONSIDERO A DATA DO ULTIMO STATUS DA OPORTUNIDADE. [A]
         * CONSIDERO A ULTIMA DATA DE OPORTUNIDADE DA AGENDA [B]
         * CONSIDERO A ULTIMA DATA DE CHAMADOS SOBRE ESTA OPORTUNIDADE. [C]
         * CONSIDERO A ULTIMA ALTERAÇÃO DE SITUAÇÃO NA AGENDA. [D]
         */
            SELECT
                MAX(tmp.insert_hora)
            INTO
                ULTIMA_INTERACAO
            FROM
                (
                    -- [A]
                    SELECT
                        MAX(oportunidade_status.insert_hora) insert_hora
                    FROM
                        oportunidade_status
                    WHERE
                        oportunidade_status.excluido = 'N' AND
                        oportunidade_status.oportunidade_id = param_oportunidade_id
                    UNION
                    -- [B]
                    SELECT
                        MAX(oportunidade_agenda.insert_hora) insert_hora
                    FROM
                        oportunidade_agenda
                    WHERE
                        oportunidade_agenda.excluido = 'N' AND
                        oportunidade_agenda.oportunidade_id = param_oportunidade_id
                    UNION
                     -- [C]
                    SELECT
                        MAX(mensagem.data_envio) insert_hora
                    FROM
                        mensagem
                    INNER JOIN chamado ON chamado.id = mensagem.chamado_id AND chamado.excluido = 'N'
                    WHERE
                        mensagem.excluido = 'N' AND
                        mensagem.chamado_id IN (SELECT oportunidade_chamado.chamado_id FROM oportunidade_chamado WHERE oportunidade_chamado.oportunidade_id = param_oportunidade_id)
                    UNION
                    -- [D]
                    SELECT
                        MAX(oportunidade_agenda.data_atividade_realizada) insert_hora
                    FROM
                        oportunidade_agenda
                    WHERE
                        oportunidade_agenda.excluido = 'N' AND
                        oportunidade_agenda.oportunidade_id = param_oportunidade_id
                    ) tmp;

            SET QTD_DIAS_SEM_INTERACAO = (SELECT DATEDIFF(CURRENT_DATE(), ULTIMA_INTERACAO));

            IF(QTD_DIAS_SEM_INTERACAO > QTD_DIAS_PARA_ESTAGNADO) THEN
                SET SITUACAO_ATUAL = ESTAGNADA;
                SET QTD_DIAS_ESTAGNADO = QTD_DIAS_SEM_INTERACAO;
                SET ESTA_ESTAGNADO = TRUE;
                SET DATA_DA_ULTIMA_INTERACAO = ULTIMA_INTERACAO;
            ELSE 
                SET DATA_DA_ULTIMA_INTERACAO = NULL;
            END IF;

            
        END IF;
        
         --                            ---
         -- VERIFICO SE ESTA ESTAGNADO ---
         --                            ---

        IF(ESTA_ESTAGNADO = FALSE AND SITUACAO_OPORTUNIDADE <> GANHA AND SITUACAO_OPORTUNIDADE <> PERDIDA) THEN

            SELECT
                MIN(tmp.data_prazo)
            INTO
                DATA_PRAZO
            FROM
            (
                SELECT
                    oportunidade_status.data_fechamento data_prazo
                FROM
                    oportunidade_status
                WHERE
                    oportunidade_status.excluido = 'N' AND
                    oportunidade_status.oportunidade_id = param_oportunidade_id AND
                    oportunidade_status.insert_hora IN (
                        SELECT
                            MAX(oportunidade_status_sub.insert_hora)
                        FROM
                            oportunidade_status oportunidade_status_sub
                        WHERE
                            oportunidade_status_sub.excluido = 'N' AND
                            oportunidade_status_sub.oportunidade_id = param_oportunidade_id
                    )
                UNION
                SELECT
                    MIN(oportunidade_agenda.data) data_prazo
                FROM
                    oportunidade_agenda
                WHERE
                    oportunidade_agenda.excluido = 'N' AND
                    oportunidade_agenda.atividade_realizada = 'N' AND
                    oportunidade_agenda.oportunidade_id = param_oportunidade_id
                UNION
                SELECT
                    DATE(MIN(chamado.data_previsao_retorno)) data_prazo
                FROM
                    chamado
                WHERE
                    chamado.excluido = 'N' AND
                    chamado.situacao NOT IN (4, 8) AND
                    chamado.id IN (
                        SELECT oportunidade_chamado.chamado_id 
                        FROM oportunidade_chamado 
                        WHERE oportunidade_chamado.oportunidade_id = param_oportunidade_id
                    )
            ) tmp;
        
            SET QTD_DIAS_ATRASADO = (SELECT DATEDIFF(CURRENT_DATE(), DATE(DATA_PRAZO) ) );
    
            IF(QTD_DIAS_ATRASADO > 0 ) THEN
                SET SITUACAO_ATUAL = ATRASADA;
            END IF;

        END IF;
        
        
        UPDATE 
            oportunidade 
        SET 
            situacao_dia = SITUACAO_ATUAL, 
            dias_estagnado = QTD_DIAS_ESTAGNADO , 
            dias_atrasado = QTD_DIAS_ATRASADO   , 
            data_ultima_interacao_estagnado = DATA_DA_ULTIMA_INTERACAO,
            data_ultima_interacao_atrasado = DATA_PRAZO
        WHERE id = param_oportunidade_id;
        
    END GERAR_OPORTUNIDADE_SITUACAO;
";
