  CREATE PROCEDURE sp_gerar_oportunidade_situacao() 
          GERAR_SITUACAO:BEGIN
   
              DECLARE OPORTUNIDADE_ID INT DEFAULT NULL;
              DECLARE DONE INT DEFAULT FALSE;
              DECLARE LISTA_OPORTUNIDADES CURSOR FOR 
   
                  SELECT id
                  FROM oportunidade
                  WHERE excluido = 'N';
   
              DECLARE CONTINUE HANDLER FOR NOT FOUND SET DONE = TRUE;
   
              OPEN LISTA_OPORTUNIDADES;
              
              PERCORRE_OPORTUNIDADES:LOOP
                  FETCH 
                      LISTA_OPORTUNIDADES
                  INTO 
                      OPORTUNIDADE_ID;
       
                  IF(DONE = TRUE) THEN
                      LEAVE PERCORRE_OPORTUNIDADES;
                  END IF;        
                  
                  CALL sp_oportunidade_situacao(OPORTUNIDADE_ID);
   
              END LOOP PERCORRE_OPORTUNIDADES;
   
          CLOSE LISTA_OPORTUNIDADES; 
  END GERAR_SITUACAO;";
  
