$updSQL[1125] = " CREATE PROCEDURE sp_gerenciar_exclusao_pasta_buscar_filhos(param_permanente CHAR(1), param_documento_pasta_id INT, param_usuario_id INT)
                            PROCESSAR_EXCLUSAO:BEGIN
                                /*
                                *   AUTOR: AYLON MURAMATSU
                                *   DATA: 22/02/2017
                                */
                                /*
                                *   ENUMS
                                */
                                
                                DECLARE PERMANENTE CHAR(1) DEFAULT 'S';
                                DECLARE LIXEIRA    CHAR(1) DEFAULT 'L';
                                DECLARE PASTA CHAR(1) DEFAULT 'P';
                                DECLARE DOCUMENTO CHAR (1) DEFAULT 'D';
                                /*
                                * VARIAVEIS
                                */
                                DECLARE SITUACAO CHAR(1) DEFAULT 'L';
                                DECLARE DOCUMENTO_PASTA_ID INT DEFAULT NULL;
                                DECLARE TIPO_ELEMENTO CHAR(1) DEFAULT NULL;
                                DECLARE done INT DEFAULT FALSE;
                                
                                /*
                                * ALGORITMO
                                */
                                IF(param_permanente = 'S') THEN
                                    SET SITUACAO = PERMANENTE;
                                ELSE
                                    SET SITUACAO = LIXEIRA;
                                END IF;
                                
                                
                                EXECUTAR_BUSCA_ATUALIZACAO:BEGIN
                                    DECLARE csr_pasta CURSOR FOR
                                        SELECT 
                                            id,
                                            tipo
                                        FROM (
                                        
                                            SELECT
                                                d.id, 
                                                DOCUMENTO AS tipo
                                            FROM
                                                documento d
                                            WHERE d.documento_pasta_id =  param_documento_pasta_id
                                            
                                            UNION
                                            
                                            SELECT 
                                                dp.id, 
                                                PASTA AS tipo
                                            FROM
                                                documento_pasta dp
                                            WHERE 
                                                dp.predecessor_id = param_documento_pasta_id
                                        ) AS elementos;
                                        
                                    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
                                    
                                    OPEN csr_pasta;
                                        LISTA_DOCUMENTOS_E_PASTAS:LOOP
                                            FETCH
                                                csr_pasta
                                            INTO 
                                                DOCUMENTO_PASTA_ID,
                                                TIPO_ELEMENTO;
                                            
                                            IF(done = TRUE) THEN
                                                LEAVE LISTA_DOCUMENTOS_E_PASTAS;
                                            END IF;

                                            IF(TIPO_ELEMENTO = PASTA) THEN
                                                SET @@session.max_sp_recursion_depth = 255;
                                                CALL sp_gerenciar_exclusao_pasta_buscar_filhos(param_permanente, DOCUMENTO_PASTA_ID, param_usuario_id);
                                               
                                               UPDATE 
                                                    documento_pasta 
                                                SET 
                                                    excluido = SITUACAO,
                                                    delete_hora = NOW(),
                                                    delete_usuario_id = param_usuario_id 
                                                WHERE 
                                                    id = DOCUMENTO_PASTA_ID;
                                                    
                                            ELSEIF(TIPO_ELEMENTO = DOCUMENTO) THEN
                                                UPDATE 
                                                    documento 
                                                SET 
                                                    excluido = SITUACAO,
                                                    delete_hora = NOW(),
                                                    delete_usuario_id = param_usuario_id 
                                                WHERE 
                                                    id = DOCUMENTO_PASTA_ID;
                                            END IF; 
                                            
                                        END LOOP LISTA_DOCUMENTOS_E_PASTAS;
                                    CLOSE csr_pasta;
                                    
                                END EXECUTAR_BUSCA_ATUALIZACAO;
                            END PROCESSAR_EXCLUSAO";

