$updSQL[1127] = "CREATE PROCEDURE sp_verificar_todas_pastas_excluidas()
                          PROCESSAR_PASTAS:BEGIN
                              /*
                              *   AUTOR: AYLON MURAMATSU
                              *   DATA: 22/02/2017
                              */
                              /*
                              *   ENUMS
                              */
                              DECLARE done INT DEFAULT FALSE;
                              DECLARE _id INT DEFAULT NULL;
                              DECLARE _tipo CHAR(1);
                              
                              EXECUTAR_BUSCA_ATUALIZACAO:BEGIN
                                  DECLARE csr_pasta CURSOR FOR
                                      SELECT 
                                          predecessor_id
                                      FROM documento_pasta dp
                                      WHERE
                                          dp.excluido = 'N'
                                          AND
                                          EXISTS
                                          (
                                               SELECT 
                                                  dp2.id
                                               FROM
                                                  documento_pasta dp2
                                               WHERE dp2.id = dp.predecessor_id
                                               AND excluido <> 'N'
                                          );
                                          
                                  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
                                  
                                  OPEN csr_pasta;
                                      LISTA_PASTAS:LOOP
                                          FETCH
                                              csr_pasta
                                          INTO 
                                              _id;
                                          
                                          IF(done = TRUE) THEN
                                              LEAVE LISTA_PASTAS;
                                          END IF;
                                          
                                          
                                          CALL sp_gerenciar_exclusao_pasta('N', _id, NULL);

                                      END LOOP LISTA_PASTAS;
                                  CLOSE csr_pasta;
                                  
                              END EXECUTAR_BUSCA_ATUALIZACAO;
                          END PROCESSAR_PASTAS";
                          
