$updSQL[1130] = "CREATE FUNCTION fn_status_projeto_atividade (param_inicio_projeto DATE, param_prazo_projeto DATE, param_conclusao_projeto DATE) RETURNS INT

    DETERMINISTIC

  BEGIN

    DECLARE PROGRAMADO INT DEFAULT 1;
    DECLARE ANDAMENTO INT DEFAULT 2;
    DECLARE CONCLUIDO INT DEFAULT 3;
    DECLARE NAO_INICIADO INT DEFAULT 4;
    DECLARE ATRASADO INT DEFAULT 5;
    DECLARE var_data_atual DATE DEFAULT CURRENT_DATE();

    IF (param_inicio_projeto > var_data_atual AND var_data_atual <= param_prazo_projeto AND param_inicio_projeto <= param_prazo_projeto AND param_conclusao_projeto IS NULL) THEN
      RETURN PROGRAMADO;
      ELSE
    IF (param_inicio_projeto > var_data_atual AND param_conclusao_projeto IS NULL AND param_prazo_projeto IS NULL) THEN
      RETURN PROGRAMADO;
      ELSE
    IF (param_inicio_projeto <= var_data_atual AND param_conclusao_projeto IS NULL AND param_prazo_projeto IS NULL) THEN
      RETURN ANDAMENTO;
      ELSE
    IF (param_inicio_projeto IS NOT NULL AND param_inicio_projeto <= var_data_atual AND param_inicio_projeto < param_prazo_projeto AND var_data_atual <= param_prazo_projeto AND param_conclusao_projeto IS NULL) THEN
      RETURN ANDAMENTO;
      ELSE
    IF (param_conclusao_projeto IS NOT NULL) THEN
      RETURN CONCLUIDO;
      ELSE
    IF (param_prazo_projeto IS NOT NULL AND var_data_atual > param_prazo_projeto) THEN
      RETURN ATRASADO;
      ELSE
    IF ((param_prazo_projeto IS NULL AND param_inicio_projeto IS NULL) OR (param_prazo_projeto IS NOT NULL AND var_data_atual <= param_prazo_projeto)) THEN
      RETURN NAO_INICIADO;

    END IF;
    END IF;
    END IF;
    END IF;
    END IF;
    END IF;
    END IF;

    RETURN 0;
END
";

