  (
    SELECT pa.porcentagem, 
      CASE
        WHEN pa.porcentagem > 0 THEN 'S'
        ELSE 'N'
      END AS iniciado,
      CASE
        WHEN pa.porcentagem = 100 THEN 'S'
        ELSE 'N'
      END AS entregue,
            pa.id as prj_atv_id
    FROM projeto_atividade pa
      INNER JOIN atividade_por_usuario apu ON pa.id = apu.projeto_atividade_id
    WHERE 
      pa.excluido = 'N'
  ) t
SET atividade_por_usuario.entregue = t.entregue, atividade_por_usuario.iniciado = t.iniciado
WHERE atividade_por_usuario.projeto_atividade_id = t.prj_atv_id
";

