    WHEN ((COALESCE(pa.porcentagem, 0) < 100 AND pa.prazo < NOW()) OR (COALESCE(pa.porcentagem, 0) = 0 AND COALESCE(pa.previsao_inicio, NOW()) < NOW())) THEN ". Atividade_Projeto_Model::ATRASADO ."
    WHEN ((COALESCE(pa.porcentagem, 0) < 100 AND pa.prazo < NOW()) OR (COALESCE(pa.porcentagem, 0) = 0 AND COALESCE(pa.previsao_inicio, NOW()) < NOW() AND pa.inicio IS NULL)) THEN ". Atividade_Projeto_Model::ATRASADO ."
    WHEN COALESCE(pa.porcentagem, 0) = 0 AND pa.inicio IS NULL AND COALESCE(TIME_TO_SEC(pa.horas_trabalhadas), 0) = 0 THEN ". Atividade_Projeto_Model::NAO_INICIADO ."
  ELSE 
    ". Atividade_Projeto_Model::ANDAMENTO ."
  END
