$updSQL[1558] = "
UPDATE 
  projeto_atividade pa
INNER JOIN 
(
  SELECT
    projeto_atividade_sintetica_pai_id,
    MIN(previsao_inicio) AS min_previsao_inicio,
    MIN(inicio) AS min_inicio,
    MAX(prazo) AS max_prazo,
    MAX(conclusao) AS max_conclusao,
    COUNT(id) AS total,
    SUM(CASE WHEN status = ". Atividade_Projeto_Model::ATRASADO ." THEN 1 ELSE 0 END) AS atrasada,
    SUM(CASE WHEN status = ". Atividade_Projeto_Model::NAO_INICIADO ." THEN 1 ELSE 0 END) AS nao_iniciada,
    SUM(CASE WHEN status = ". Atividade_Projeto_Model::CONCLUIDO ." THEN 1 ELSE 0 END) AS concluido,
    CAST(SUM(porcentagem) / COUNT(id) AS UNSIGNED) AS saldo_percentual
  FROM 
    projeto_atividade
  WHERE 
    projeto_atividade_sintetica_pai_id IN (
      SELECT
        pa2.id
      FROM
        (select * from projeto_atividade) pa2
      WHERE 
        pa2.tipo_atividade = 'S'
    )
  AND excluido = 'N'
  GROUP BY 
    projeto_atividade_sintetica_pai_id
) pa_tmp ON pa_tmp.projeto_atividade_sintetica_pai_id = pa.id
SET pa.status = 
  CASE
    WHEN pa_tmp.atrasada > 0 THEN ". Atividade_Projeto_Model::ATRASADO ."
    WHEN pa_tmp.nao_iniciada = total THEN ". Atividade_Projeto_Model::NAO_INICIADO ."
    WHEN pa_tmp.concluido = total THEN ". Atividade_Projeto_Model::CONCLUIDO ."
  ELSE 
    ". Atividade_Projeto_Model::ANDAMENTO ."
  END,
  pa.inicio = pa_tmp.min_inicio,
  pa.prazo = pa_tmp.max_prazo,
  pa.previsao_inicio = pa_tmp.min_previsao_inicio,
  pa.conclusao = CASE WHEN pa_tmp.concluido = total THEN pa_tmp.max_conclusao ELSE null END,
  pa.porcentagem = pa_tmp.saldo_percentual
";

