$updSQL[1641] = "
CREATE PROCEDURE `sp_atualizar_numero_solicitacoes` ()
BEGIN
	DECLARE done INT DEFAULT FALSE;
	DECLARE p_id INT;
    DECLARE cursor_processo CURSOR FOR SELECT id FROM processo ORDER BY id;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

	OPEN cursor_processo;
	read_loop: LOOP
	FETCH cursor_processo INTO p_id;
	IF done THEN
	  LEAVE read_loop;
	END IF;
		SET @temp_numero := 0;
		UPDATE solicitacao
		SET numero=@temp_numero:=@temp_numero+1
		WHERE versao_processo_id IN (
			SELECT id FROM versao_processo WHERE processo_id = p_id
		)
		ORDER BY id;
	END LOOP;
	CLOSE cursor_processo;
END";

