/**
 * Nesta rotina, atualizo as permissões dos usuários que não são colaboradores, alterando a empresa_filial_id para 0.
 */
$updSQL['20200615_chm45796_reprocessa_permissoes_usuarios_1'] = " DROP TABLE IF EXISTS usersPermissions; ";

// cria tabela temporária e ignora chave duplicada para o usuário via group by
$updSQL['20200615_chm45796_reprocessa_permissoes_usuarios_2'] = "
  CREATE TABLE IF NOT EXISTS usersPermissions
  SELECT usuario_permissao_distinct.usuario_id, usuario_permissao_distinct.chave, 0 as empresa_filial_id FROM
  (
    select up.usuario_id, up.chave from usuario
    inner join usuario_permissao up on up.usuario_id = usuario.id
    where
      usuario.tipo != 2
      and usuario.excluido = 'N'
    GROUP BY up.usuario_id, up.chave
  )
  AS usuario_permissao_distinct;
";

// apaga permissoes dos usuarios envolvidos na tabela temporária "usersPermissions"
$updSQL['20200615_chm45796_reprocessa_permissoes_usuarios_3'] = "
