$updSQL['BPM5161_reprocessamento_sequencia_orcamentos'] = function() {
  /**
   * Reprocessamento da sequência de orçamentos de oportunidades
   */

  // Oportunidades que tenham orçamentos vinculados
  $sql_oportunidades = " SELECT id AS orcamento_id, oportunidade_id FROM oportunidade_orcamento ORDER BY oportunidade_id ASC; ";

  $oportunidades = DB::query($sql_oportunidades);

  $sql_update = " UPDATE oportunidade_orcamento SET sequencia = :sequencia WHERE id = :orcamento_id ";

  foreach($oportunidades as $key_oportunidade => $oportunidade) {
    if ($key_oportunidade == 0 || $oportunidade['oportunidade_id'] != $oportunidades[$key_oportunidade-1]['oportunidade_id']) {
      // Inicio (caso seja a primeira inciência do loop) ou reinicio a sequência caso o loop chegue em uma nova oportunidade
      $sequencia = 1;
    }else{
      $sequencia++;
    }

    $params_update = [];
    $params_update[':sequencia'] = $sequencia;
    $params_update[':orcamento_id'] = $oportunidade['orcamento_id'];

    DB::exec($sql_update, $params_update);
  }
};

