$updSQL['CHM51501_recalculo_percentual_conclusao_projeto'] = "
  UPDATE 
      projeto p 
  INNER JOIN
  (
      SELECT 
          projeto_id,
          CAST(SUM(porcentagem) / COUNT(id) AS UNSIGNED) AS porcentagem_concluido,
          CAST(100 - SUM(porcentagem) / COUNT(id) AS UNSIGNED) AS saldo_percentual
      FROM 
          projeto_atividade
      WHERE
          excluido = 'N'
      AND projeto_atividade.tipo_atividade = 'A'
      AND projeto_atividade.cancelada != 'S'
      GROUP BY 
          projeto_id
  ) pa ON p.id = pa.projeto_id
  SET 
      p.porcentagem_concluido = pa.porcentagem_concluido,
      p.saldo_percentual = pa.saldo_percentual
";

