$updSQL['BPM5168_reprocessar_ordem_oportunidades'] = function() {

  $etapas_funil_vendas = " SELECT id FROM funil_vendas_etapa ";

  $query_etapas_funil_vendas = DB::query($etapas_funil_vendas);

  foreach($query_etapas_funil_vendas as $key_etapa_funil => $etapa_funil) {

    $sql = "
        UPDATE oportunidade AS op
        JOIN
        (
          SELECT 
            op.id,
            @rownum := @rownum + 1 AS posicao
          FROM 
            oportunidade op, (SELECT @rownum := -1) r
          WHERE
            op.excluido = 'N'
            AND op.funil_vendas_etapa_id = :funil_vendas_etapa_id
          ORDER BY op.ordem
        ) AS r ON op.id = r.id
        SET op.ordem = r.posicao
    ";

    $params = [];
    $params[':funil_vendas_etapa_id'] = $etapa_funil['id'];

    DB::exec($sql, $params);
  }
};

$updSQL['20210629_chm53653_correcao_base_dados'] = function() {

  try {

    // Obtenho todas as listas de quadros
    $sql_listas = "
      SELECT id FROM tarefa_quadro_lista
    ";

    $query_listas = DB::query($sql_listas);

    // Atualizo a coluna 'ordem' da tabela 'tarefa_quadro_lista_cartao'
    foreach($query_listas as $key => $item) {
      $sql = "
        UPDATE tarefa_quadro_lista_cartao AS t
        JOIN
        (
            SELECT t.*, 
            @rownum := @rownum + 1 AS posicao
          FROM tarefa_quadro_lista_cartao t, (SELECT @rownum := -1) r
          WHERE
            t.excluido = 'N'
            AND t.tarefa_quadro_lista_id = :tarefa_quadro_lista_id
          ORDER BY t.ordem
        ) AS r ON t.id = r.id
        SET t.ordem = r.posicao
      ";

      $params = [];
      $params['tarefa_quadro_lista_id'] = $item['id'];

      DB::exec($sql, $params);
    }
  }
  catch(BusinessException $e) {
    throw $e;
  }
};

