  $sql_lista = " SELECT id FROM menu_personalizado WHERE excluido = 'N' ";

  $query_lista = DB::query($sql_lista);

  if (DB::has_rows($query_lista)) {
    $sql_update = " UPDATE menu_personalizado SET ordem = :ordem WHERE id = :id ";

    foreach ($query_lista as $key => $item) {
      $params = [];
      $params[':ordem'] = $key;
      $params[':id'] = $item['id'];

      DB::exec($sql_update, $params);
    }
  }
};

