$updSQL['CHM62433_reprocessamento_central_pendencias_chamados_encerrados'] = function() {
  $select = "
    SELECT central_pendencias.id AS pendencia_id 
    FROM central_pendencias
    INNER JOIN chamado ON chamado.id = central_pendencias.chamado_id
    WHERE chamado.situacao IN (4,8)
  ";

  $query = DB::query($select);

  if (DB::has_rows($query)) {
    $delete = " DELETE FROM central_pendencias WHERE id = :pendencia_id ";

    foreach($query as $key => $item) {
      $params = [];
      $params[':pendencia_id'] = $item['pendencia_id'];

      DB::exec($delete, $params);
    }
  }
};

