/**
 * Reprocessamento nos apontamentos para serem convertidos/listados na tela de Apontamentos Simples
 */
$updSQL['BPM8214_reprocessamento_apontamentos'] = function() {
  $sql = "
    SELECT
      apontamento_projeto_atividade.id AS apontamento_projeto_atividade_id,
      apontamento_projeto_atividade.apontamento_projeto_id,
      apontamento_projeto_atividade.projeto_atividade_id
    FROM
      apontamento_projeto_atividade
      INNER JOIN apontamento_projeto ON apontamento_projeto.id = apontamento_projeto_atividade.apontamento_projeto_id AND apontamento_projeto.excluido = 'N'
      INNER JOIN projeto_atividade ON projeto_atividade.id = apontamento_projeto_atividade.projeto_atividade_id AND projeto_atividade.excluido = 'N'
    WHERE
      apontamento_projeto_atividade.excluido = 'N'
  ";

  $query = DB::query($sql);

  if (DB::has_rows($query)) {
    $apontamento_projeto_dao = new Apontamento_Projeto_DAO;

    foreach ($query as $key_query => $row_query) {
      $apontamento_projeto_model = $apontamento_projeto_dao->popular($row_query['apontamento_projeto_id']);

      $apontamento_projeto_dao->salvar_tabela_apontamento_projeto_simples($row_query['apontamento_projeto_atividade_id'], $row_query['projeto_atividade_id'], $apontamento_projeto_model);
    }
  }
};

