$updSQL['BPM19548_popular_tabelas_etiqueta'] = "
DROP TEMPORARY TABLE IF EXISTS `tmp_etiqueta`;

CREATE TEMPORARY TABLE IF NOT EXISTS `tmp_etiqueta` (
  `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `tarefa_etiqueta_id` int(11) NOT NULL,
  `insert_hora` datetime DEFAULT NULL,
  `update_hora` datetime DEFAULT NULL,
  `insert_usuario_id` int(11) DEFAULT NULL,
  `update_usuario_id` int(11) DEFAULT NULL,
  `nome` varchar(200) DEFAULT NULL,
  `cor_fundo` varchar(10) DEFAULT NULL,
  `cor_fonte` varchar(10) DEFAULT NULL,
  `tarefa_quadro_id` int(11) NOT NULL
);

INSERT INTO tmp_etiqueta (
    tarefa_etiqueta_id,
    insert_hora,
    update_hora,
    insert_usuario_id,
    update_usuario_id,
    nome,
    cor_fundo,
    cor_fonte,
    tarefa_quadro_id
)
SELECT 
    tarefa_etiqueta_bkp.id,
    tarefa_etiqueta_bkp.insert_hora,
    tarefa_etiqueta_bkp.update_hora,
    tarefa_etiqueta_bkp.insert_usuario_id,
    tarefa_etiqueta_bkp.update_usuario_id,
    tarefa_etiqueta_bkp.nome,
    tarefa_etiqueta_bkp.cor_fundo,
    tarefa_etiqueta_bkp.cor_fonte,
    tarefa_quadro.id AS tarefa_quadro_id
FROM tarefa_etiqueta_bkp
LEFT JOIN tarefa_quadro ON (tarefa_quadro.excluido = 'N')
WHERE tarefa_etiqueta_bkp.excluido = 'N';

INSERT INTO etiqueta (
    id,
    insert_hora,
    update_hora,
    insert_usuario_id,
    update_usuario_id,
    pai_tabela,
    pai_id,
    nome,
    cor_fundo,
    cor_fonte
)
SELECT
    id,
    insert_hora,
    update_hora,
    insert_usuario_id,
    update_usuario_id,
    'tarefa_quadro' AS pai_tabela,
    tarefa_quadro_id AS pai_id,
    nome,
    cor_fundo,
    cor_fonte
FROM tmp_etiqueta;

INSERT INTO etiqueta_usuario_tipo (
    etiqueta_id,
    usuario_tipo
)
SELECT
    id,
    2 AS usuario_tipo
FROM tmp_etiqueta;

INSERT INTO etiqueta_referencia (etiqueta_id, referencia_tabela, referencia_id)
SELECT
	tmp_etiqueta.id AS etiqueta_id,
	'tarefa_quadro_lista_cartao' AS referencia_tabela,
	tarefa_etiqueta_cartao_bkp.tarefa_quadro_lista_cartao_id AS referencia_id
FROM tarefa_etiqueta_cartao_bkp
INNER JOIN tarefa_quadro_lista_cartao ON (tarefa_quadro_lista_cartao.id = tarefa_etiqueta_cartao_bkp.tarefa_quadro_lista_cartao_id)
INNER JOIN tmp_etiqueta ON (tmp_etiqueta.tarefa_etiqueta_id = tarefa_etiqueta_cartao_bkp.tarefa_etiqueta_id AND tmp_etiqueta.tarefa_quadro_id = tarefa_quadro_lista_cartao.tarefa_quadro_id);

DROP TEMPORARY TABLE IF EXISTS `tmp_etiqueta`;
";

